@js:

//远程key链接
  let remoteUrls = 
[
"https://cdn.jsdelivr.net/gh/Xwite/autoCI@autoBuild/coco_keys.json",

"https://raw.githubusercontent.com/Xwite/autoCI/autoBuild/coco_keys.json", 
  "http://raw.githubusercontents.com/Xwite/autoCI/main/coco_keys.json",

  ];

function fetchRemoteKeys() {

 let remoteKeys = batchFetch(remoteUrls);

  let hint = "自动获取远程密钥...",
      update_success = "密钥自动更新成功，刷新",
      update_fail= "密钥暂未更新，请反馈或者等待远程自动刷新";
  java.log(hint);
  checkKeyFormat(remoteKeys);
  java.log(remoteKeys);

  let keys = source.getVariable();
  if (!keys||!keys.includes(remoteKeys)) {
   source.setVariable(remoteKeys);
   java.toast(update_success);
   } else {
   	java.toast(update_fail)
   }
  return remoteKeys;
}

decrypt = (key, word) => java.aesBase64DecodeToString(String(word), String(key), "AES/ECB/PKCS7Padding", "") + "";

function checkKeyFormat(keys) {
	if (!/C_DATA/.test(keys)) throw ("密钥格式错误");
	}

function parseJSON(s) {
  checkKeyFormat(s);
  return JSON.parse(s)
}

base64Decode = base64 => java.base64Decode(base64) + ""; //封装解密接口


function decode(base64, keys) {
	if(!keys) throw("密钥不可为空");
  //网站使用两个密钥兜底
  let decrypted = "null",
      word = base64Decode(base64); // java.log("AES(base64)密文：" + word);
  //aes解密

  for (let idx = 0; idx < keys.length; idx++) {
      decrypted = decrypt(keys[idx], word);
      if (decrypted != "null") return decrypted;
  }
  fetchRemoteKeys();
  throw(`解密失败`)
}

function decodeImage(html) {
  let keys = source.getVariable();
  keys = parseJSON(keys);
  //密钥保存在书源变量
  let C_DATA_KEYS = keys["C_DATA"],
      enc_code1_keys = keys["enc_code1"],
      enc_code2_keys = keys["enc_code2"]; //加密后的图片信息

  let C_DATA = html.match(/C_DATA\s*=\s*\S([\s\S]+?)['"]/);
  if(!C_DATA) throw("图片数据获取失败！！");
  C_DATA = C_DATA[1];
  eval(decode(C_DATA, C_DATA_KEYS));
  let pics_list = [];

  if (!image_info.img_type) {
    let path;

    if (typeof mh_info.enc_code2 == "undefined") {
      path = mh_info.imgpath;
    } else {
      path = decode(mh_info.enc_code2, enc_code2_keys);
    }

    if (typeof mh_info.enc_code1 == "undefined") {
      totalImageCount = mh_info.totalimg;
    } else {
      totalImageCount = decode(mh_info.enc_code1, enc_code1_keys);
    }

    let servers = [mh_info.domain];

    try {
      eval(html.match(/(var __JS_READLINE[\s\S]+?)<\/script>/)[1]);
      servers.push(__JS_READLINE.domain || __JS_READLINE.data[0].domain);
    } catch (e) {} //java.log("服务器:" + servers.join());


    for (let i = parseInt(mh_info.startimg); i <= totalImageCount; i++) {
      pics_list.push("https://" + servers[0] + "/comic/" + encodeURI(path) + ("0000" + i).slice(-4) + ".jpg");
    }
  } else {
    let urls__direct = base64Decode(image_info.urls__direct).split("|SEPARATER|");

    if (image_info.img_type == 1) {
      pics_list = urls__direct.map(url => url + ".webp");
    } else {
      pics_list = urls__direct;
    }
  }

  return pics_list.toImg(check);
}

//尝试从多个来源下载同一文件 失败自动下一个
function batchFetch(urls){for(url of urls){c=java.ajax(url);if(!c.includes("Exception"))return c}throw(` 文件下载失败❌`)}


var options={headers:{Referer:baseUrl}};
//String.add(options)
function add(url,options){return!options?url:`${url},${JSON.stringify(options)}`;}String.prototype.add=function(){return add(this,arguments[0]);};
Object.prototype.add=function(){return add(this.toString(),arguments[0]);};

function check(url){let domain=String(source.getKey()).replace(/^.*?\./,"");if(url.includes(domain))return url.add(options);return url}

//Array.toImg(checkUrl)
function toImg(imgs,handle){return imgs.map(src=>`<img src="${handle?handle(src):src}"/>`).join("\n");}Array.prototype.toImg=function(){return toImg(this,arguments[0]);};

if (source.getVariable() == null) {
  fetchRemoteKeys();
  decodeImage(src);
} else {
  decodeImage(src);
}

